import React, { useState, useEffect } from 'react';
import './App.css';

interface VoteCount {
  yea?: number;
  nay?: number;
  aye?: number;
  no?: number;
  present: number;
  notVoting: number;
}

interface VoteData {
  date: string;
  congress: string;
  rollCallNumber: string;
  billNumber: string;
  voteQuestion: string;
  billTitle: string;
  voteType: string;
  status: string;
  votes: VoteCount;
}

interface PaginationData {
  currentPage: number;
  totalPages: number;
  totalResults: number;
}

function App() {
  const [votes, setVotes] = useState<VoteData[]>([]);
  const [activeVote, setActiveVote] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [pagination, setPagination] = useState<PaginationData>({
    currentPage: 1,
    totalPages: 1,
    totalResults: 0
  });

  // Initial data for display while loading
  const initialVoteData: VoteData[] = [
    {
      date: "Jun 06, 2025, 10:15 AM",
      congress: "119th Congress, 1st Session",
      rollCallNumber: "156",
      billNumber: "H. R. 2966",
      voteQuestion: "On Passage",
      billTitle: "American Entrepreneurs First Act",
      voteType: "Yea-And-Nay",
      status: "Passed",
      votes: {
        yea: 217,
        nay: 190,
        present: 0,
        notVoting: 25
      }
    },
    {
      date: "Jun 04, 2025, 02:01 PM",
      congress: "119th Congress, 1st Session",
      rollCallNumber: "149",
      billNumber: "H. Res. 458",
      voteQuestion: "On Agreeing to the Resolution",
      billTitle: "Providing for consideration of the bills (H.R. 2483) the SUPPORT for Patients and Communities Reauthorization Act; (H.R. 2931) the Save SBA from Sanctuary Cities Act; (H.R. 2966) the American Entrepreneurs First Act; and (H.R. 2987) the CEASE Act",
      voteType: "Recorded Vote",
      status: "Passed",
      votes: {
        aye: 217,
        no: 208,
        present: 0,
        notVoting: 7
      }
    }
  ];

  useEffect(() => {
    fetchVotes(1);
  }, []);

  const fetchVotes = async (page: number) => {
    try {
      setLoading(true);
      setError(null);

      // If the backend is not running, use the initial data for demonstration
      if (process.env.NODE_ENV === 'development' && !process.env.REACT_APP_USE_BACKEND) {
        setTimeout(() => {
          setVotes(initialVoteData);
          setPagination({
            currentPage: 1,
            totalPages: 16,
            totalResults: 156
          });
          setLoading(false);
        }, 1000);
        return;
      }

      const response = await fetch(`http://localhost:3001/api/votes?page=${page}`);

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();

      if (data.votes && Array.isArray(data.votes)) {
        setVotes(data.votes);
        setPagination(data.pagination);
        // Reset active vote to the first one when changing pages
        setActiveVote(0);
      } else {
        throw new Error('Invalid data format received from server');
      }
    } catch (err) {
      console.error('Error fetching vote data:', err);
      setError('Failed to load vote data. Using sample data instead.');
      // Fallback to initial data
      setVotes(initialVoteData);
    } finally {
      setLoading(false);
    }
  };

  // If no votes available, display loading or error state
  if (loading) {
    return (
      <div className="App">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading vote data...</p>
        </div>
      </div>
    );
  }

  if (error && votes.length === 0) {
    return (
      <div className="App">
        <div className="error-container">
          <h2>Error</h2>
          <p>{error}</p>
          <button onClick={() => fetchVotes(1)} className="retry-button">Retry</button>
        </div>
      </div>
    );
  }

  // If we have votes, display them
  const currentVote = votes[activeVote];

  // Determine vote labels based on vote type
  const hasYeaNay = 'yea' in currentVote.votes || 'nay' in currentVote.votes;
  const voteLabels = hasYeaNay
    ? { positive: "yea", negative: "nay" } 
    : { positive: "aye", negative: "no" };

  return (
    <div className="App">
      <div className="vote-card">
        <div className="vote-header">
          <div className="vote-date">{currentVote.date}</div>
          <div className="vote-congress">{currentVote.congress}</div>
        </div>

        {votes.length > 1 && (
          <div className="vote-toggle">
            {votes.map((vote, index) => (
              <button 
                key={vote.rollCallNumber}
                className={`toggle-button ${activeVote === index ? 'active' : ''}`} 
                onClick={() => setActiveVote(index)}
              >
                Vote #{vote.rollCallNumber}
              </button>
            ))}
          </div>
        )}

        <div className="vote-main">
          <h1>Roll Call Number: {currentVote.rollCallNumber}</h1>
          <h2>Bill Number: {currentVote.billNumber}</h2>
          <div className="vote-question">Vote Question: {currentVote.voteQuestion}</div>

          <div className="bill-info">
            <h3>{currentVote.billTitle}</h3>
            <div className="vote-type">Vote Type: {currentVote.voteType}</div>
            <div className={`vote-status ${currentVote.status.toLowerCase()}`}>
              Status: {currentVote.status}
            </div>
          </div>

          <button className="details-button">View Details</button>
        </div>

        <div className="vote-results">
          <h3>Votes</h3>
          <div className="vote-counts">
            <div className="vote-count yea">
              <span className="label">{voteLabels.positive}:</span>
              <span className="number">{
                hasYeaNay ? currentVote.votes.yea : currentVote.votes.aye
              }</span>
            </div>
            <div className="vote-count nay">
              <span className="label">{voteLabels.negative}:</span>
              <span className="number">{
                hasYeaNay ? currentVote.votes.nay : currentVote.votes.no
              }</span>
            </div>
            <div className="vote-count present">
              <span className="label">present:</span>
              <span className="number">{currentVote.votes.present}</span>
            </div>
            <div className="vote-count not-voting">
              <span className="label">not voting:</span>
              <span className="number">{currentVote.votes.notVoting}</span>
            </div>
          </div>
          <button className="details-button">View Details</button>
        </div>

        {pagination.totalPages > 1 && (
          <div className="pagination">
            <button 
              className="pagination-button"
              disabled={pagination.currentPage === 1}
              onClick={() => fetchVotes(pagination.currentPage - 1)}
            >
              Previous
            </button>

            <span className="pagination-info">
              Page {pagination.currentPage} of {pagination.totalPages}
            </span>

            <button 
              className="pagination-button"
              disabled={pagination.currentPage === pagination.totalPages}
              onClick={() => fetchVotes(pagination.currentPage + 1)}
            >
              Next
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
